// App.jsx
import React, { useState, useEffect } from 'react';

const App = () => {
  // Simula los datos directamente en el componente
  const data = [
    {
      ean: 'EAN1',
      nombre_producto: 'Producto 1',
      sku: 'SKU1',
      market: 'Market1',
      rango_precios: '100.0 - 120.0',
    },
    {
      ean: 'EAN2',
      nombre_producto: 'Producto 2',
      sku: 'SKU2',
      market: 'Market2',
      rango_precios: '130.0 - 150.0',
    },
    {
      ean: 'EAN3',
      nombre_producto: 'Producto 3',
      sku: 'SKU3',
      market: 'Market3',
      rango_precios: '90.0 - 110.0',
    },
    // Puedes agregar más objetos según sea necesario
  ];

  const [filteredData, setFilteredData] = useState(data);
  const [visibleIndices, setVisibleIndices] = useState(Array(data.length).fill(0));

  useEffect(() => {
    const intervals = data.map((_, index) => {
      return setInterval(() => {
        // Ocultar un producto de cada conjunto cada segundo
        setVisibleIndices(prevIndices => {
          const newIndices = [...prevIndices];
          newIndices[index] = newIndices[index] < filteredData.length - 1 ? newIndices[index] + 1 : newIndices[index];
          return newIndices;
        });
      }, 1000);
    });

    return () => {
      intervals.forEach(clearInterval);
    };
  }, [data, filteredData]);

  const handleFilterChange = (filterTerm) => {
    // Filtrar productos por nombre
    const filteredProducts = data.filter(item =>
      item.nombre_producto.toLowerCase().includes(filterTerm.toLowerCase())
    );

    setFilteredData(filteredProducts);
    setVisibleIndices(Array(filteredProducts.length).fill(0));
  };

  return (
    <div>
      {/* Filtro por nombre */}
      <input
        type="text"
        placeholder="Filtrar por nombre"
        onChange={(e) => handleFilterChange(e.target.value)}
      />

      {/* Renderizar productos filtrados */}
      {filteredData.map((item, index) => (
        <div key={item.ean} style={{ display: index === visibleIndices[index] ? 'block' : 'none' }}>
          <p>{item.nombre_producto}</p>
          <p>{item.rango_precios}</p>
          <p>{`En ${item.market}`}</p>
        </div>
      ))}
    </div>
  );
};

export default App;
